import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { CepServiceService } from '../service/cep-service.service';
import { FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-endereco',
  templateUrl: './endereco.component.html',
  styleUrls: ['./endereco.component.scss']
})
export class EnderecoComponent {

  constructor(private cepService: CepServiceService) { }

  consultarCEP(cep: any, form: any) {
    cep = cep.target.value;
    if (cep != "") {
      this.cepService.buscar(cep).subscribe(res => {
        this.popularDados(res, form);
      });
    }
  }

  popularDados(dados: any, form: any) {
    form.setValue({
      bairro: dados.bairro,
      complemento: dados.complemento,
      ddd: dados.ddd,
      rua: dados.logradouro,
      cidade: dados.localidade,
      UF: dados.uf,
    })
  }

}
<form #form="ngForm">
  <mat-form-field>
    <input #cep matInput placeholder="CEP" id="cep" name="cep" value="" (blur)="consultarCEP($event, form)">
  </mat-form-field>

  <div>

    <mat-form-field>
      <input ngModel matInput #bairro placeholder="bairro" id="bairro" name="bairro" value="">
    </mat-form-field>

    <mat-form-field>
      <input ngModel matInput #complemento placeholder="complemento" id="complemento" name="complemento" value="">
    </mat-form-field>

    <mat-form-field>
      <input ngModel matInput #ddd placeholder="ddd" id="ddd" name="ddd" value="">
    </mat-form-field>

    <mat-form-field>
      <input ngModel matInput #rua placeholder="rua" id="rua" name="rua" value="">
    </mat-form-field>

    <mat-form-field>
      <input ngModel matInput #cidade placeholder="cidade" id="cidade" name="cidade" value="">
    </mat-form-field>

    <mat-form-field>
      <input ngModel matInput #UF placeholder="UF" id="UF" name="UF" value="">
    </mat-form-field>

  </div>
</form>
